import React, {useEffect} from "react";
import {useParams} from "react-router-dom"

function Study({data, fnObj, studyDeck}){
  let result

  const deckId = useParams();

  useEffect(()=>{
    // const val = data.deck.find((item)=>deckId.deckId==item.id);
    // console.log("VAL", val)
    // setData({[data.topic]: data.topic=val});
    // setData({...data});
    fnObj.studyDeckSetter(deckId.deckId);
  }, [])
  console.log('STUDY DECK', studyDeck.cards[0].front)

  console.log('SIDE TO DISPLAY', data.sideToDisplay)
  if(studyDeck !== undefined){
    result=(
      <>
        <h1>Study: {studyDeck.name} </h1>
        <div className="card">
          <div className="card-title px-2">
            <h4>Card {studyDeck.cards[data.counter].id} of {studyDeck.cards.length}</h4>
          </div>
          <p className="card-body">
            {data.sideToDisplay}
          </p>
          <div className="row px-3">
            <button onClick={fnObj.flipHandler()} className="m-2 btn btn-secondary col-1">Flip</button>
            {data.isNextBtnOn ?
            <button onClick={fnObj.nextHandler()} className="m-2 btn btn-primary col-1">Next</button> : null}
          </div>
        </div>
      </>
    )
  }
  else{return null};

  return result;
}



export default Study;
