import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom"

function Study({deck}){
  let counter = 0;

  const deckId = useParams();

  const [topic, setTopic] = useState(deck.find((item)=>deckId.deckId==item.id))
  const [sideToDisplay, setDisplay] = useState(topic.cards[counter].front)
  //console.log("DECK ID", deckId)
  //console.log('DECK', deck)
  //const topic = deck.find((item)=>deckId.deckId==item.id) //double equals coerces string to num
  console.log('TOPIC', topic)
  //let sideToDisplay = topic.cards[counter].front;

  const flipHandler = () =>{
    //console.log('DID IT WORK?')
    if(sideToDisplay===topic.cards[counter].front){
      console.log("FLIPPED TO THE BACK")
      return setDisplay(topic.cards[counter].back);   
    }
    if(sideToDisplay===topic.cards[counter].back){
      console.log("FLIPPED TO THE FRONT")
      return setDisplay(topic.cards[counter].front);
    }
  }
  console.log("SIDE TO DISPLAY", sideToDisplay)
  function renderCard(){
    return(
      <>
        <h1>Study: {topic.name} </h1>
        <div className="card">
          <div className="card-title">
            <h4>Card {topic.cards[counter].id} of {topic.cards.length}</h4>
          </div>
          <div className="card-body">
            {sideToDisplay}
          </div>
          <button onClick={flipHandler} className="m-2 btn btn-secondary col-1">Flip</button>
        </div>
      </>
    )
  }
  let result = renderCard();

  useEffect(()=>{
    renderCard();
    flipHandler();
  }, [setDisplay()])
  //console.log(deckId.deckId)
  return result
}



export default Study;
