import React, {useState, useEffect} from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import {Switch, Route} from "react-router-dom";
import CreateDeckBtn from './home/CreateDeckBtn'
import Deck from "./home/Deck"
import {listDecks, deleteDeck, readCard} from "../utils/api/index"
import Study from "./home/Study"




function Layout() {

  const [deck, setDeck] = useState([])
  const [card, setCard] = useState([])

  const fnObj={
    deleteHandler: function(event){
      if(window.confirm("Are you sure you want to delete this deck?")){
        deleteDeck(event.target.id);
        fnObj.deckFetcher();
      } 
    },
    deckFetcher: async function(){
      const deckSet = await Promise.resolve(listDecks());
      //console.log("PROMISE HANDLER")
      return setDeck(deckSet);
    },
    cardFetcher: async function(){
      const cardSet = await Promise.resolve(readCard());
      return setCard(cardSet);
    }
  }


useEffect(()=>{
  fnObj.deckFetcher();
  //fnObj.cardFetcher();
}, [])

  return (
    <>
      <Header />
      <div className="container">
        <Switch>
          <Route path={"/"}>
            <CreateDeckBtn />
            <Deck deck={deck} fnObj={fnObj}/>
          </Route>
          <Route exact={true} path={"/decks/:deckId/study"}>
            <Study deck={deck} />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch> 
      </div>
    </>
  );
}

export default Layout;
