import React, {useState, useEffect} from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import {Switch, Route} from "react-router-dom";
import CreateDeckBtn from './home/CreateDeckBtn'
import Deck from "./home/Deck"
import {listDecks, deleteDeck} from "../utils/api/index"
import Study from "./home/Study"
import BreadCrumb from "./BreadCrumb";




function Layout() {


  let initialState={
    deck: [Promise.resolve(listDecks())],
    isNextBtnOn: false,
    topic: {},
    counter: 0,
    sideToDisplay: "",
  }


  let [data, setData] = useState({...initialState})
  console.log("DATA", data)

  const fnObj={
    deleteHandler: function(event){
      if(window.confirm("Are you sure you want to delete this deck?")){
        deleteDeck(event.target.id);
        fnObj.deckFetcher();
      }
    },
    deckFetcher: async function(){
      const deckSet = await Promise.resolve(listDecks());
      setData({[data.deck]: data.deck=deckSet});
      return setData({...data});
    },
    nextHandler: function(){
      setData({
        [data.counter]: data.counter+=1, 
        [data.isNextBtnOn]: data.isNextBtnOn=false,
        [data.sideToDisplay]: data.sideToDisplay=data.topic.cards[data.counter].front
      })
      return setData({...data})
    },
    flipHandler: function(){
      if(data.sideToDisplay===data.topic.cards[data.counter].front){
        setData({
          [data.sideToDisplay]: data.sideToDisplay=data.topic.cards[data.counter].back,
          [data.isNextBtnOn]: data.isNextBtnOn = true,
        });
        return setData({...data});   
      }
      if(data.sideToDisplay===data.topic.cards[data.counter].back){
        setData({
          [data.sideToDisplay]: data.sideToDisplay=data.topic.cards[data.counter].front,
          [data.isNextBtnOn]: data.isNextBtnOn = false,
        });
        return setData({...data});
      }
    },
    topicSetter: function(id){
      let val = data.deck.find((item)=>id==item.id);
      setData({[data.topic]: data.topic=val});
      return setData({...data})
    }
  }

  console.log("TOPIC", data.topic)

  console.log("DATA.DECK", data.deck)

  useEffect(()=>{
    fnObj.deckFetcher();
  }, [])

  return (
    <>
      <Header />
      <div className="container">
        <Switch>
          <Route exact={true} path={"/"}>
            <CreateDeckBtn />
            <Deck data={data} fnObj={fnObj} />
          </Route>
          <Route exact={true} path={"/decks/:deckId/study"}>
            <BreadCrumb data={data}/>
            <Study data={data} fnObj={fnObj}/>
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch> 
      </div>
    </>
  );
}

export default Layout;
