import React, {useEffect} from "react";
import {useParams, useHistory} from "react-router-dom"

function Study({fnObj, studyDeck, sideToDisplay, setDisplay, counter, isNextBtnOn, setCounter}){
  let result
  const deckId = useParams();
  const history=useHistroy();

  useEffect(()=>{
    fnObj.studyDeckSetter(deckId.deckId);
    setCounter(0);
  }, [])


  if(counter===studyDeck.length && isNextBtnOn===true){
    if(window.confirm("Want to restart deck? If not, you will be returned to the home page")){
    }
    else{
      history.push("/")
    }
  }

  if(studyDeck !== undefined){
    setDisplay(studyDeck.cards[counter].front)

    result=(
      <>
        <h1>Study: {studyDeck.name} </h1>
        <div className="card">
          <div className="card-title px-2">
            <h4>Card {cardOfCounter+1} of {studyDeck.cards.length}</h4>
          </div>
          <p className="card-body">
            {sideToDisplay}
          </p>
          <div className="row px-3">
            <button onClick={fnObj.flipHandler} className="m-2 btn btn-secondary col-1">Flip</button>
            {isNextBtnOn ?
            <button onClick={fnObj.nextHandler} className="m-2 btn btn-primary col-1">Next</button> : null}
          </div>
        </div>
      </>
    )
  }
  else{
    return null
  };

  return result;
}



export default Study;
