import React, {useEffect} from "react";
import {useParams, useHistory} from "react-router-dom"

function Study({fnObj, studyDeck, sideToDisplay, setDisplay, counter, isNextBtnOn, setCounter, setNextBtn}){
  let result
  const deckId = useParams();
  const history=useHistory();


  useEffect(()=>{
    setCounter(0);
    fnObj.studyDeckSetter(deckId.deckId);
  }, [])




  //if(studyDeck !== undefined){




    result=(
      <>
        <h1>Study: {studyDeck.name} </h1>
        <div className="card">
          <div className="card-title px-2">
            <h4>Card {counter+1} of {studyDeck.cards.length}</h4>
          </div>
          <p className="card-body">
            {sideToDisplay}
          </p>
          <div className="row px-3">
            <button onClick={fnObj.flipHandler} className="m-2 btn btn-secondary col-1">Flip</button>
            {isNextBtnOn ?
            <button onClick={fnObj.nextHandler} className="m-2 btn btn-primary col-1">Next</button> : null}
          </div>
        </div>
      </>
    )
  //}
  // else{
  //   return null
  // };
  if(counter===studyDeck.cards.length-1 && isNextBtnOn===true){
    if(window.confirm("Press OK to restart deck and CANCEL to return home")){
      setCounter(0);
      setNextBtn(false);
    }
    else{
      history.push("/");
      setCounter(0);
      setNextBtn(false);
    }
  }

  return result;
}



export default Study;
