import React, {useEffect, useState} from "react";
import {useParams, useHistory, useRouteMatch} from "react-router-dom"
import NotEnoughCards from '../NotEnoughCards'
import {readDeck} from "../../utils/api/index"

function Study({fnObj, studyDeck, counter, isNextBtnOn, setCounter, setNextBtn}){
  let result
  const url = useRouteMatch();
  const deckId = useParams();
  const history=useHistory();
  console.log(counter)
  console.log('DECK ID', deckId.deckId)

  let [sideToDisplay, setDisplay] = useState(undefined);

  useEffect(()=>{
    fnObj.studyDeckSetter(deckId.deckId);
    setDisplay(studyDeck.cards[counter].front)
  }, [])
  //console.log('STUDY DECK ID', studyDeck.id)

  // let [counter, setCounter] = useState(0);
  // let [isNextBtnOn, setNextBtn] = useState(false);

  const flipHandler = () => {
    if(sideToDisplay===studyDeck.cards[counter].front){
      setDisplay(studyDeck.cards[counter].back);
      setNextBtn(true);
      return   
    }
    if(sideToDisplay===studyDeck.cards[counter].back){
      setDisplay(studyDeck.cards[counter].front);
      setNextBtn(false);
      return
    }
  }

  const nextHandler = () => {
    if(counter<studyDeck.cards.length-1){
      setCounter(counter+=1);
      setNextBtn(false);
      setDisplay(studyDeck.cards[counter].front);
    }
    else{
      if(window.confirm("Press OK to restart deck and CANCEL to return home")){
        setNextBtn(false);
        setDisplay(studyDeck.cards[0].front);
      }
      else{
        history.push("/");
        setNextBtn(false);
      }
      setCounter(0);
    }
  }






  if(studyDeck.id == deckId.deckId){


    result=(
      <div className="container">
        <div className="row">
          <h1>Study: {studyDeck.name} </h1>
        </div>
        {studyDeck.cards.length>=3 ?
        <div className="row">
          <div className="card w-100">
            <div className="card-body">
              <h5 className="card-title pl-20">
                Card {counter+1} of {studyDeck.cards.length}
              </h5>
              <p className="card-text">
                {sideToDisplay}
              </p>
              <div>
                <button onClick={flipHandler} className=" btn btn-secondary">Flip</button>
                {isNextBtnOn ?
                <button onClick={nextHandler} className="mx-2 btn btn-primary">Next</button> : null}
              </div>
            </div>
          </div> 
        </div> : 
        <NotEnoughCards studyDeck={studyDeck} />}
      </div>
    )
  }
  else{
    return null
  };


  return result;
}



export default Study;
