import React, {useEffect} from "react";
import {useParams} from "react-router-dom"
import NotEnoughCards from '../NotEnoughCards'

function Study({fnObj, studyDeck, sideToDisplay, counter, isNextBtnOn}){
  let result
  const deckId = useParams();


  useEffect(()=>{
    fnObj.studyDeckSetter(deckId.deckId);
  }, [])




  if(studyDeck !== undefined){


    result=(
      <div className="container">
        <div className="row">
          <h1>Study: {studyDeck.name} </h1>
        </div>
        {studyDeck.cards.length>=3 ?
        <div className="row">
          <div className="card wm-100">
            <div className="card-title px-2">
              <h4>Card {counter+1} of {studyDeck.cards.length}</h4>
            </div>
            <p className="card-body">
              {sideToDisplay}
            </p>
            <div className="px-3">
              <button onClick={fnObj.flipHandler} className="m-2 btn btn-secondary col-1">Flip</button>
              {isNextBtnOn ?
              <button onClick={fnObj.nextHandler} className="m-2 btn btn-primary col-1">Next</button> : null}
            </div>
          </div> 
        </div> : 
        <NotEnoughCards studyDeck={studyDeck} />}
      </div>
    )
  }
  else{
    return null
  };


  return result;
}



export default Study;
