import React, {useState} from "react";
import {Switch, Route, Link} from "react-router-dom"
import Study from "./Study"

function Deck({deck, fnObj}){
  //console.log(deck)

  const result = deck.map(item => (
      <div key={item.id} className="card mt-4">
        <div className="card-body">
          <h3 className="card-title">
            {item.name}
          </h3>
          <p className="card-text">
            {item.description}
          </p>
        </div>
        <div className="container m-3">
          <div className="row justify-content-start">
            <Link className="col-1 btn btn-secondary">View</Link>
            <Link to={`/decks/${item.id}/study`} className="col-1 btn btn-primary">Study</Link>
            <button id={item.id} onClick={fnObj.deleteHandler} className="col-1 btn btn-danger">Delete</button>
          </div>
        </div>
      </div>
  ));

  return(
    <>
      <Switch>
        <Route exact={true} path={"/"}>
          {result}
        </Route>
        <Route path={"/decks/:deckId/study"}>
          <Study deck={deck}/>
        </Route>
      </Switch>
    </>
  )
}


export default Deck;
