import React, {useEffect} from "react";
import {useParams} from "react-router-dom"

function Study({data, fnObj}){
  let result

  const deckId = useParams();
  // const val = data.deck.find((item)=>deckId.deckId==item.id);
  // console.log("VAL", val)
  // setData({[data.topic]: data.topic=val});
  // setData({...data});
  // console.log('DECK ID', deckId.deckId)
  //console.log('TOPIC', data.topic)

  if(data.topic!==undefined){
    result=(
      <>
        <h1>Study: {data.topic.name} </h1>
        <div className="card">
          <div className="card-title px-2">
            <h4>Card {data.topic.cards[data.counter].id} of {data.topic.cards.length}</h4>
          </div>
          <p className="card-body">
            {data.sideToDisplay}
          </p>
          <div className="row px-3">
            <button onClick={fnObj.flipHandler()} className="m-2 btn btn-secondary col-1">Flip</button>
            {data.isNextBtnOn ?
            <button onClick={fnObj.nextHandler()} className="m-2 btn btn-primary col-1">Next</button> : null}
          </div>
        </div>
      </>
    )
  }
  else{return null};

  return result
}



export default Study;
