import React, {useEffect} from "react";
import {useParams} from "react-router-dom"
import AddCard from '../AddCard'

function Study({fnObj, studyDeck, sideToDisplay, counter, isNextBtnOn}){
  let result
  const deckId = useParams();
  //const history=useHistory();


  useEffect(()=>{
    fnObj.studyDeckSetter(deckId.deckId);
  }, [])




  if(studyDeck !== undefined){
    // if(counter===studyDeck.cards.length-1 && isNextBtnOn===true){
    //   if(window.confirm("Press OK to restart deck and CANCEL to return home")){
    //     setCounter(0);
    //     setNextBtn(false);
    //   }
    //   else{
    //     history.push("/");
    //     setCounter(0);
    //     setNextBtn(false);
    //   }
    // }

    result=(
      <>
        <div className="row">
          <h1 className="px-12">Study: {studyDeck.name} </h1>
        </div>
        {studyDeck.cards.length>=3 ?
        <div className="row">
          <div className="card">
            <div className="card-title px-2">
              <h4>Card {counter+1} of {studyDeck.cards.length}</h4>
            </div>
            <p className="card-body">
              {sideToDisplay}
            </p>
            <div className="px-3">
              <button onClick={fnObj.flipHandler} className="m-2 btn btn-secondary col-1">Flip</button>
              {isNextBtnOn ?
              <button onClick={fnObj.nextHandler} className="m-2 btn btn-primary col-1">Next</button> : null}
            </div>
          </div> 
        </div> : 
        <AddCard studyDeck={studyDeck} />}
      </>
    )
  }
  else{
    return null
  };


  return result;
}



export default Study;
