import React, {useState, useEffect} from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import {Switch, Route} from "react-router-dom";
import CreateDeckBtn from './home/CreateDeckBtn'
import Deck from "./home/Deck"
import {listDecks, deleteDeck} from "../utils/api/index"
import Study from "./home/Study"
import BreadCrumb from "./BreadCrumb";




function Layout() {


  let initialState={
    isNextBtnOn: false,
    counter: 0,
  }
  const [deck, setDeck] = useState([]);
  const [studyDeck, setStudyDeck] = useState(undefined);
  const [sideToDisplay, setDisplay] = useState();

  let [data, setData] = useState({...initialState})
  console.log("DATA", data)

  const fnObj={
    deleteHandler: function(event){
      if(window.confirm("Are you sure you want to delete this deck?")){
        deleteDeck(event.target.id);
        fnObj.deckFetcher();
      }
    },
    deckFetcher: async function(){
      const deckSet = await Promise.resolve(listDecks());
      return setDeck(deckSet);
    },
    nextHandler: function(){
      setData({
        [data.counter]: data.counter+=1, 
        [data.isNextBtnOn]: data.isNextBtnOn=false,
      })
      setData({...data});
      setDisplay(studyDeck.cards[data.counter].front);
    },
    flipHandler: function(){
      if(sideToDisplay===studyDeck.cards[data.counter].front){
        setDisplay(studyDeck.cards[data.counter].back);
        setData({[data.isNextBtnOn]: data.isNextBtnOn = true,});
        return setData({...data});   
      }
      if(sideToDisplay===studyDeck.cards[data.counter].back){
        setDisplay(studyDeck.cards[data.counter].front);
        setData({[data.isNextBtnOn]: data.isNextBtnOn = false});
        return setData({...data});
      }
    },
    studyDeckSetter: function(id){
      const item = deck.find((item)=>id==item.id);
      setStudyDeck(item);
      //setDisplay(studyDeck.cards[data.counter].front)
    }
  }


  useEffect(()=>{
   fnObj.deckFetcher();
  }, [])

  return (
    <>
      <Header />
      <div className="container">
        <Switch>
          <Route exact={true} path={"/"}>
            <CreateDeckBtn />
            <Deck 
              deck={deck} 
              fnObj={fnObj} 
            />
          </Route>
          <Route exact={true} path={"/decks/:deckId/study"}>
            <BreadCrumb 
              studyDeck={studyDeck}
            />
            <Study 
              data={data} 
              fnObj={fnObj} 
              studyDeck={studyDeck}
              sideToDisplay={sideToDisplay}
              setDisplay={setDisplay}
            />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch> 
      </div>
    </>
  );
}

export default Layout;
