import React from "react";

function EditDeck({studyDeck}){

  let result=(
    <section className="container">
      <h2 className="row">Edit Deck</h2>
      <form onSubmit={fnObj.createDeckSubmitHandler}>
        <label className="row">
          <h5>Name:</h5>
          <textarea
            className="w-100"
            name="name"
            type="text"
            value={studyDeck.name}
            onChange={fnObj.createDeckChangeHandler}
          />
        </label>
        <label className="row">
          <h5>Description:</h5>
          <textarea 
          className="w-100"
            name="description"
            type="text"
            value={studyDeck.description}
            onChange={fnObj.createDeckChangeHandler}
          />
        </label>
        <div className="row">
          <Link to={"/"} className="btn btn-secondary">Cancel</Link>
          <button className="btn btn-primary mx-2" type="submit">Submit</button>
        </div>
      </form>
    </section>
  )
  return result;

}


export default EditDeck;
