import React, {useEffect} from "react"
import {Link} from "react-router-dom"
import {listCards} from '../utils/api/index'


function AddCard({studyDeck, fnObj, addCardData, setAddCardData}){
  let result="";
  let cardId=0;

  let allCards=[]

  const findAllCards = async()=>{
    allCards = await listCards()
  }

  findAllCards();

  console.log(allCards)

  for(let card in studyDeck.cards){
    if(studyDeck.cards[card].id>cardId){cardId=studyDeck.cards[card].id};
  }
  cardId+=1;


  useEffect(()=> { 
    setAddCardData({
      [addCardData.deckId]: addCardData.deckId=studyDeck.id,
      [addCardData.deckId]: addCardData.id=cardId,
    }); 
    setAddCardData({...addCardData})
  }, [])

  result=(
    <div className="container">
      <div className="row">
        <h3>{studyDeck.name}: Add Card</h3>
      </div>
      <form onSubmit={fnObj.addCardSubmitHandler}>
        <label>
          <h5 className="row">Front:</h5>
          <textarea 
            className="row" 
            name="front"
            type="text"
            placeholder="Front side of card"
            onChange={fnObj.addCardFormChangeHandler}
          />
          <h5 className="row">Back:</h5>
          <textarea
            className="row"
            width="100%"
            name="back"
            type="text"
            placeholder="Back side of card"
            onChange={fnObj.addCardFormChangeHandler}
          />
        </label>
        <div className="row">
          <Link to={"/"} className="btn btn-secondary">Done</Link>
          <button className="btn btn-primary mx-5" type="submit">Save</button>
        </div>
      </form>
    </div>
  )


  return result;
}

export default AddCard;
