import React from "react";
import {Link} from "react-router-dom"

function CardForm({studyDeck, fnObj, cardId}){
  let allCards=[]
  let card={};

  const url = `http://localhost:5000/cards`;

  async function fetchJson(url, options) {
    try {
      const response = await fetch(url, options);
      if (response.status < 200 || response.status > 399) {
        throw new Error(`${response.status} - ${response.statusText}`);
      }
      return await response.json();
    } catch (error) {
      if (error.name !== "AbortError") {
        throw error;
      }
    }
  }
  async function getCardsArray(){
    allCards = await fetchJson()
    console.log(allCards)
    for(let item in allCards){
      if(allCards[item].id===cardId){card=allCards[item].id}
    }
  }
  getCardsArray()


  console.log('CARD', card)

  let result
  if(cardId===undefined){
    result=(
      <>      
        <form onSubmit={fnObj.addCardSubmitHandler}>
          <label className="row">
            <h5>Front:</h5>
            <textarea 
              className="w-100" 
              name="front"
              type="text"
              placeholder="Front side of card"
              onChange={fnObj.addCardFormChangeHandler}
            />
          </label>
          <label className="row">
            <h5>Back:</h5>
            <textarea
              className="w-100"
              name="back"
              type="text"
              placeholder="Back side of card"
              onChange={fnObj.addCardFormChangeHandler}
            />
          </label>
          <div className="row">
            <Link to={"/"} className="btn btn-secondary">Done</Link>
            <button className="btn btn-primary mx-5" type="submit">Save</button>
          </div>
        </form>
      </>
    )
  }
  else{
    result=(
      <>      
        <form onSubmit={fnObj.addCardSubmitHandler}>
          <label className="row">
            <h5>Front:</h5>
            <textarea 
              className="w-100" 
              name="front"
              type="text"
              value={studyDeck.cards.cardId.front}
              onChange={fnObj.addCardFormChangeHandler}
            />
          </label>
          <label className="row">
            <h5>Back:</h5>
            <textarea
              className="w-100"
              name="back"
              type="text"
              value={studyDeck.cards.cardId.back}
              onChange={fnObj.addCardFormChangeHandler}
            />
          </label>
          <div className="row">
            <Link to={"/"} className="btn btn-secondary">Done</Link>
            <button className="btn btn-primary mx-5" type="submit">Save</button>
          </div>
        </form>
      </>
    )
  }
  return result;

}


export default CardForm;
