import React, {useEffect} from "react"
import {Link} from "react-router-dom"
import {listCards, fetchJson} from "../utils/api/index"


function AddCard({studyDeck, fnObj, addCardData, setAddCardData}){
  let result="JSX";
  let cardId=0;
  let allCards=[]
  const url = `http://localhost:5000/cards`;


  async function fetchJson(url, options) {
    try {
      const response = await fetch(url, options);
      if (response.status < 200 || response.status > 399) {
        throw new Error(`${response.status} - ${response.statusText}`);
      }
      return await response.json();
    } catch (error) {
      if (error.name !== "AbortError") {
        throw error;
      }
    }
  }
  async function getBiggerNumber(){
    const response = fetchJson(url);
    allCards = await Promise.resolve(response);
    for(let card in allCards){
      if(allCards[card].id>cardId){cardId=allCards[card].id};
    }
    cardId+=1;
  }
  getBiggerNumber();






  useEffect(()=> { 

    setAddCardData({
      [addCardData.deckId]: addCardData.deckId=studyDeck.id,
      [addCardData.deckId]: addCardData.id=cardId,
    }); 
    setAddCardData({...addCardData})
  }, [])

  result=(
    <div className="container">
      <div className="row">
        <h3>{studyDeck.name}: Add Card</h3>
      </div>
      <form onSubmit={fnObj.addCardSubmitHandler}>
        <label>
          <h5 className="row">Front:</h5>
          <textarea 
            className="row" 
            name="front"
            type="text"
            placeholder="Front side of card"
            onChange={fnObj.addCardFormChangeHandler}
          />
          <h5 className="row">Back:</h5>
          <textarea
            className="row"
            width="100%"
            name="back"
            type="text"
            placeholder="Back side of card"
            onChange={fnObj.addCardFormChangeHandler}
          />
        </label>
        <div className="row">
          <Link to={"/"} className="btn btn-secondary">Done</Link>
          <button className="btn btn-primary mx-5" type="submit">Save</button>
        </div>
      </form>
    </div>
  )


  return result;
}

export default AddCard;
