import React, {useState, useEffect} from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import {Switch, Route} from "react-router-dom";
import CreateDeckBtn from './home/CreateDeckBtn'
import Deck from "./home/Deck"
import {listDecks, deleteDeck} from "../utils/api/index"
import Study from "./home/Study"
import BreadCrumb from "./BreadCrumb";




function Layout() {


  let initialState={
    deck: [],
    isNextBtnOn: false,
    topic: {},
    counter: 0,
    sideToDisplay: "",
  }


  let [data, setData] = useState({...initialState})
  console.log("DATA", data)
  const [deck, setDeck] = useState([])

  const fnObj={
    deleteHandler: function(event){
      if(window.confirm("Are you sure you want to delete this deck?")){
        deleteDeck(event.target.id);
        fnObj.deckFetcher();
      } 
    },
    deckFetcher: async function(){
      const deckSet = await Promise.resolve(listDecks());
      setData({[data.deck]: data.deck=deckSet});
      return setData({...data});
    },
    nextHandler: function(){
      setData({
        [data.counter]: data.counter+=1, 
        [data.isNextBtnOn]: isNextBtnOn=true,
        [data.sideToDisplay]: data.sideToDisplay=topic.cards[counter].front
      })
      setCounter(counter+=1);
      setNextBtn(false);
      setDisplay(topic.cards[counter].front);
      //return renderCard();
      return setData({...data})
    }
  }
  console.log("DECK", deck)
  console.log("DATA.DECK", data.deck)

  useEffect(()=>{
    fnObj.deckFetcher();
  }, [])

  return (
    <>
      <Header />
      <div className="container">
        <Switch>
          <Route exact={true} path={"/"}>
            <CreateDeckBtn />
            <Deck data={data} fnObj={fnObj}/>
          </Route>
          <Route exact={true} path={"/decks/:deckId/study"}>
            <BreadCrumb deck={deck}/>
            <Study data={data} fnObj={fnObj}/>
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch> 
      </div>
    </>
  );
}

export default Layout;
