import React, {useState} from "react";
import {Link} from "react-router-dom";

function EditDeck({studyDeck}){

  const [editDeck, setEditDeck] = useState(studyDeck);
  console.log('EDIT DECK', editDeck);

  const editDeckChangeHandler=({target})=>{
    setEditDeck({
      ...editDeck,
      [target.name]: target.value
    })
  }

  const editDeckSubmitHandler=(event)=>{

  }

  let result=(
    <section className="container">
      <h2 className="row">Edit Deck</h2>
      <form onSubmit={editDeckSubmitHandler}>
        <label className="row">
          <h5>Name:</h5>
          <textarea
            className="w-100"
            name="name"
            type="text"
            value={studyDeck.name}
            onChange={editDeckChangeHandler}
          />
        </label>
        <label className="row">
          <h5>Description:</h5>
          <textarea 
          className="w-100"
            name="description"
            type="text"
            value={studyDeck.description}
            onChange={editDeckChangeHandler}
          />
        </label>
        <div className="row">
          <Link to={"/"} className="btn btn-secondary">Cancel</Link>
          <button className="btn btn-primary mx-2" type="submit">Submit</button>
        </div>
      </form>
    </section>
  )
  return result;

}


export default EditDeck;
